import React, { useEffect, useState } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import "./reservation.css";
import Form from "react-bootstrap/Form";
import Table from 'react-bootstrap/Table';

export default function Reservation() {
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [tableNum, setTableNum] = useState("");
  const [userName, setUserName] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log(token);
    if (token) {
      try {
        const decoded = jwtDecode(token); // Decoding the JWT
        console.log(decoded); // Debug: Log the decoded token to verify its contents
        if (decoded.email) {
          // Check if the decoded token has an email field
          setUserName(decoded.email); // Set userName to the email from the token
        } else {
          console.error("JWT does not contain email:");
        }
      } catch (error) {
        console.error("JWT decoding error:", error);
      }
    } else {
      console.log("No token found in localStorage");
    }
  }, []);

  function resData(e) {
    e.preventDefault();

    const newReservation = {
      date,
      time,
      tableNum,
      userName,
    };
    axios
      .post("http://localhost:8070/reservation/book", newReservation)
      .then(() => {
        alert("reservation successful");
      })
      .catch((err) => {
        alert(err);
      });
  }
  return (
    <div>
      <div className="content">
        <div className="forms">
          <div className="form-details">
            <h2>Make Reservations</h2>
          </div>
          <div className="form-content">
            <form onSubmit={resData}>
              <div className="inputs">
                <label>Date:</label>
                <input
                  type="date"
                  name="date"
                  required
                  onChange={(e) => {
                    setDate(e.target.value);
                  }}
                />
                <br />
              </div>
              <div className="inputs">
                <label>Time:</label>
                <Form.Select
                  aria-label="Default select example"
                  onChange={(e) => {
                    setTime(e.target.value);
                  }}
                >
                  <option>Open this select menu</option>
                  <option value="09:00:00">9 - 10</option>
                  <option value="10:00:00">10 - 11</option>
                  <option value="11:00:00">11 - 12</option>
                  <option value="12:00:00">12 - 1</option>
                  <option value="13:00:00">1 - 2</option>
                  <option value="14:00:00">2 - 3</option>
                  <option value="11:00:00">3 - 4</option>
                  <option value="11:00:00">4 - 5</option>
                </Form.Select>
              </div>

              <div className="inputs">
                <label for="tables">Table Number:</label>
                <Form.Select
                  aria-label="Default select example"
                  onChange={(e) => {
                    setTime(e.target.value);
                  }}
                >
                  <option>Open this select menu</option>
                  <option value="001">001</option>
                  <option value="002">002</option>
                  <option value="003">003</option>
                  <option value="004">004</option>
                  <option value="005">005</option>
                  <option value="006">006</option>
                  <option value="007">007</option>
                  <option value="008">008</option>
                  <option value="009">009</option>
                  <option value="010">010</option>
                  <option value="011">011</option>
                  <option value="012">012</option>
                </Form.Select>
                <br />
              </div>
              <button type="submit" value="Submit">
                Book Now
              </button>
            </form>
            </div>
            <div className="form-details">
            <h2>Previous Reservations</h2>
          </div>
          <Table striped="columns">
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
      {items.map((items) => {
              return (
                <>
                  <tr>
                    <td>{items.item}</td>
                    <td>Rs.{items.price}</td>
                    <td>
                      <button onClick={() => decrese(items._id)} name="minus">(-)</button>
                    </td>
                    <td>{items.numOfItems}</td>
                    <td>
                      <button onClick={() => increse(items._id)} name="plus">(+)</button>
                    </td>
                    <td>
                      <button onClick={() => removeItem(items._id)} name="remove">Remove</button>
                    </td>
                  </tr>
                </>
              );
            })}
      </tbody>
    </Table>
          
        </div>
      </div>
    </div>
  );
}
